            let searchsort = Number(getItem("searchsort", "0"));
            let {
              naturalSort,
              usingSort
            } = $.require("methods");

            if (searchsort > 0) {
              if (searchsort == 1) {
                naturalSort(list, "name");
              } else if (searchsort == 2) {
                usingSort.setkey("name");
                usingSort.get(list)
              }
            }
            function removeObjectWithPropertyName(list, sourceName) {
              return list.filter(item => item.name !== sourceName);
            }
            let name = source.name ? source.name : source;
            log("name:" + name);
            if (source.searchable && listi != -1) {
              //list.splice(list.indexOf(source), 1);
              list = removeObjectWithPropertyName(list, name)
              list.unshift(source);
            }

            let rules = [];


            for (let it of list) {
              rules.push({
                title: it.name,
                search_url: "hiker://empty?page=fypage&searchTerms=**",
                searchFind: "js:" + $.toString((item, mark) => {
