                    let extra = {
                      //id: cid,
                      vodId: id,
                      isPolymerize: true,
                      img: it.vod_pic,
                      sname: item.name,
                      skey: item.key,
                    }

                    if (skipEr) {
                      extra = Object.assign(extra, pextra);

                      url = $().lazyRule((source, id) => {
                        let url;
                        try {
                          let DrpyManage = GM.defineModule("DrpyManage");
                          let drpy = DrpyManage.getBySource(source);
                          url = JSON.parse(drpy.detail(id)).list[0].vod_play_url.split("$")[1];
                        } catch (e) {
                          url = id.split("@@")[0];
                        }
                        return $.require("videoUrl").parse(url, id, source);
                      }, item, id);
                      cid = id;
                    }

                    d.push({
                      title: it.vod_name,
                      desc: it.vod_remarks,
                      content: it.vod_content,
                      url: url,
                      pic_url: it.vod_pic,
                      col_type: "movie_3",
                      extra: extra
                    });
                  }
                  setResult(d);
                }, it, mark)
              });
            }
            return JSON.stringify(rules);
          } catch (e) {
            log(e.toString());
          }
        }, ui_config.mark)
      }
    }]);
  }
