    if (typeof str == 'number') {
      str = String(str);
    }
    if (!str || str === prefix) return undefined;
    str = str.replace(/\//g, ' ').replace(/\s+/g, " ");
    if (/[:：]/g.test(str)) {
      str = str.replace(/[:：]/g, ':');
    }
    return /[:：]/.test(str) ? str : `${prefix}:${str}`;
