const JSEngine = com.example.hikerview.service.parser.JSEngine;
const runtimeConfig = GM.defineModule("runtimeConfig");
const drpyMap = new Map();
const GMkey = module.importParam;
//log("初始化DrpyManage");
const proxySemaphore = new java.util.concurrent.Semaphore(3);
const T4Adapter = $.require("T4Adapter");
const T1Adapter = $.require("T1Adapter");

function buildJsEnv(ticket) {
  let code = String.raw`
    // const my_rule = '
    const MY_RULE = ${my_rule};
    const my_rule = JSON.stringify(MY_RULE);
    const MY_TICKET = "${ticket || ""}";
    eval(getJsPlugin());
    eval(getJsLazyPlugin());
    `;
  return code;
}

function sync(func, sp) {
  return new org.mozilla.javascript.Synchronizer(func, sp || {});
}

function createDrpy(key) {
  JSEngine.getInstance().evalJS(buildJsEnv(MY_TICKET) + "\n!" + $.toString((key, GMkey, MY_TICKET) => {
    let drpy = $.require("drpy", key);
    GM.has(GMkey, (DrpyManage) => {
      DrpyManage.put(key, drpy);
    });
  }, key, GMkey, MY_TICKET) + ";\n", "", false);
}

function createT3(source) {
  let key = source.key;
  createDrpy(key);
  let drpy = drpyMap.get(key);
  drpy.init(runtimeConfig.getAbsolutePath(source.ext));
  try {
    if (drpy.getRule("proxy_rule")) {
      let Proxy = $.require("LocalProxy");
      let proxyUrl = Proxy.startProxy(MY_RULE.title, GM.getSelfKey());
      putMyVar("Proxy_Url", proxyUrl);
    }
  } catch (e) { }
  return drpy;
}

function createNewDrpy(source) {
  source = JSON.parse(JSON.stringify(source));
  if (source.type === 4) {
    let t4 = new T4Adapter(source);
    t4.init();
    drpyMap.set(source.key, t4);
    return t4;
  } else if (source.type === 1) {
    let t1 = new T1Adapter(source);
    t1.init();
    drpyMap.set(source.key, t1);
    return t1;
  } else if (source.type === 0) {
    let T0Adapter = $.require("T0Adapter");
    let t0 = new T0Adapter(source);
    t0.init();
    drpyMap.set(source.key, t0);
    return t0;
  } else if (source.api === "csp_XBPQ") {
    let XBPQAdapter = $.require("XBPQAdapter");
    if (typeof source.ext === "string") {
      source.ext = runtimeConfig.getAbsolutePath(source.ext);
    }
    let xbpq = new XBPQAdapter(source);

    xbpq.init();
    drpyMap.set(source.key, xbpq);
    return xbpq;
  } else if (source.api === "csp_AppYsV2") {
    let AppYsV2Adapter = $.require("AppYsV2Adapter");

    let appYsV2 = new AppYsV2Adapter(source);
    appYsV2.init();
    drpyMap.set(source.key, appYsV2);
    return appYsV2;
  } else {
    return createT3(source);
  }
}

function get(key) {
  return sync(() => {
    //log(drpyMap.size)
    if (drpyMap.has(key)) {
      return drpyMap.get(key);
    }
    if (drpyMap.size >= 5) {
      //log("请求:" + key)
      del(Array.from(drpyMap.keys()).at(0));
    }
    let source = runtimeConfig.getAllSources().find(v => v.key === key);
    let drpy = createNewDrpy(source);
    return drpy;
  }, this).call();
}

function has(key) {
  if (drpyMap.has(key)) {
    return drpyMap.get(key);
  } else {
    return null;
  }
}

//source.ext必须为绝对路径
function getBySource(source) {
  return sync(() => {
    let key = source.key;
    //log(drpyMap.size)
    if (drpyMap.has(key)) {
      return drpyMap.get(key);
    }
    if (drpyMap.size >= 5) {
      log("请求:" + key)
      del(Array.from(drpyMap.keys()).at(0));
    }
    let drpy = createNewDrpy(source);
    return drpy;
  }, this).call();
}

function put(key, drpy) {
  sync(() => drpyMap.set(key, drpy), this).call();
}

function runProxy(drpy, args) {
  if (typeof drpy.proxy === "function") {
    try {
      //let time1 = Date.now();
      proxySemaphore.acquire();
      //let time2 = Date.now();
      //log("1等待时间:" + (time2 - time1) + "ms");
      let res = drpy.proxy.apply(drpy, args);
      //let time3 = Date.now();
      //log("2执行代理耗时:" + (time3 - time2) + "ms");
      //log("3总耗时耗时:" + (time3 - time1) + "ms");

      return res;
    } finally {
      proxySemaphore.release();
    }
  }
}

function del(key) {
  sync(() => {
    //log("删除" + key);
    drpyMap.delete(key);
    /*if (drpyMap.has(key)) {

    }*/
  }, this).call();
}
