  }

  function popTitle(input) {
    let list = input?input:searchoncount;
    return "启用搜索源:" + list + "个 共:" + canSearchSource.length + "个搜索源"
  }

  let searchKey = "";
  let inputBox;
  hikerPop.selectBottomRes({
