function reorderArrayByOrder(array, order) {
  // 创建一个映射，将key值映射到原始数组中的对象
  let map = new Map(array.map(obj => [obj.key, obj]));
  let reorderedArray = []; // 重新排序后的数组
  let removedKeys = new Set(); // 存储需要移除的key值
  // 根据order数组来重新排列array数组
  order.forEach((item, i) => {
    if (item.index === -1) {
      // 如果index是-1，将key添加到移除列表中
      removedKeys.add(item.key);
    } else {
      // 如果index不是-1，则将对象添加到结果数组中的指定位置
      let obj = map.get(item.key);

      if (obj && item.hasOwnProperty("img") && item.img) {
        obj.img = item.img;
      }
      if (obj && item.hasOwnProperty("title") && item.title) {
        obj.title = item.title;
      }
      if (obj) {
        reorderedArray[i] = obj;
      }
    }
  });
  // 添加那些不在order数组中的对象，并排除移除列表中的项
  array.forEach(obj => {
    if (!removedKeys.has(obj.key) && !reorderedArray.includes(obj)) {
      reorderedArray.push(obj);
    }
  });
  // 过滤掉结果数组中的undefined项，并返回
  return reorderedArray.filter(obj => obj !== undefined);
}
/**
 * 生成一个随机颜色的十六进制表示。
 * @return {string} 返回一个形如 '#RRGGBB' 的随机颜色字符串。
 */
var getRangeColors = function () {
  return '#' + ('00000' + (Math.random() * 0x1000000 << 0).toString(16)).substr(-6);
};

/**
 * 深度合并多个对象的属性。
 * @param {Object} target - 被合并的对象，第一个参数，合并的结果将以此对象为基础。
 * @returns {Object} - 合并后的对象。
 */
function deepMerge(target) {
  function isObject(obj) {
    return obj && typeof obj === 'object' && !Array.isArray(obj);
  }
  for (var i = 1; i < arguments.length; i++) {
    var source = arguments[i];
    for (var key in source) {
      if (isObject(source[key])) {
        if (!target[key]) {
          target[key] = {};
        }
        deepMerge(target[key], source[key]);
      } else {
        target[key] = source[key];
      }
    }
  }
  return target;
}

/**
 * 从源对象中深度删除指定的属性。
 * @param {Object} source - 要处理的源对象。
 * @param {Object} exc - 指定要排除的属性，以键值对形式指定。
 * @returns {Object} - 返回一个新的对象，不包含指定要排除的属性。
 */
function deepOmit(source, exc) {
  const result = {};
  // 遍历顶层键
  for (let key in source) {
    if (source.hasOwnProperty(key)) {
      // 检查顶层键是否应该被排除
      if (!(exc.hasOwnProperty(key) && exc[key] === true)) {
        // 如果顶层键对应的值是对象，并且有子排除规则，则递归调用 deepOmit
        if (typeof source[key] === 'object' && source[key] !== null && exc[key]) {
          result[key] = deepOmit(source[key], exc[key]);
        } else if (typeof source[key] !== 'object' || source[key] === null || !exc[key]) {
          // 如果顶层键对应的值不是对象，或者没有子排除规则，直接复制值
          result[key] = source[key];
        }
      }
    }
  }
  return result;
}

/**
 * 判断两个对象是否相等，忽略指定的键。
 * @param {*} x - 第一个对象。
 * @param {*} y - 第二个对象。
 * @param {Array} excludeKeys - 需要忽略比较的键数组。
 * @returns {boolean} - 如果两个对象相等（考虑嵌套对象）且不包含指定的键，则返回true；否则返回false。
 */
function isEqual(x, y, excludeKeys) {
  excludeKeys = excludeKeys != undefined ? excludeKeys : [];
  const ok = Object.keys;
  const tx = typeof x;
  const ty = typeof y;
  if (x && y && tx === "object" && tx === ty) {
    const keysX = ok(x).filter(key => !excludeKeys.includes(key));
    const keysY = ok(y).filter(key => !excludeKeys.includes(key));
    if (keysX.length !== keysY.length) {
      return false;
    }
    return keysX.every((key) => isEqual(x[key], y[key], excludeKeys));
  }
  return x === y;
}

let themeManage = {
  name: "theme.json",
  path: "hiker://files/rules/DrpyHiker/",
  save(content) {
    saveFile(this.path + this.name, content);
  },
  init() {
    let json = {};
    if (!fileExist(this.path + this.name)) {
      this.save("{}");
    }
    try {
      json = JSON.parse(readFile(this.path + this.name));
    } catch (e) {
      json = {};
      this.save("{}");
    }
    return json;
  },
  has(ts) {
    let json = this.init();
    let sjson = deepMerge({}, json);
    let keys = Object.keys(ts);
    keys.forEach(k => {
      if (!json.hasOwnProperty(k)) {
        json[k] = ts[k];
      }
    })
    if (Object.keys(sjson).length == 0) {
      json = deepMerge(json, sjson);
      this.save(JSON.stringify(json));
      return json;
    }
    if (!keys.every(key => sjson.hasOwnProperty(key))) {
      this.save(JSON.stringify(json));
    }
    return json;
  },
  get(key) {
    let json = this.init();
    if (key != undefined) {
      if (typeof key != "string") {
        json = this.has(key);
        return json;
      }
    } else {
      return json;
    }
  },
  set(key, value) {
    let json = this.init();
    if (key != undefined) {
      json[key] = value;
    }
    this.save(JSON.stringify(json));
  }
}

