function getLineShow(ef) {
  //log(ef)
  let obj = {
    s: ef,
    line: ef.from.indexOf(ef.from[ef.line]),
    lines: ef.from.length,
    from: ef.from[ef.line],
    plays: ef.plays[ef.line],
    spage: ef.spagenum,
    max: ef.max[ef.line]
  }
  function Template(template, data) {
    // 用于执行字符串中的表达式
    function evaluateExpression(expr) {
      try {
        // 使用 with 语句将 data 对象放入当前作用域
        // 这样可以在表达式中直接使用 data 的属性
        with (data) {
          return eval(expr);
        }
      } catch (e) {
        return expr; // 如果表达式无法计算，则返回原始字符串
      }
    }
    // 正则表达式匹配 { 表达式 } 格式的占位符
    return template.replace(/{(.*?)}/g, (match, expr) => {
      //console.log(expr)
      // 执行表达式
      return evaluateExpression(expr);
    });
  }
  let lshow = Template(ui_config.er.线路显示, obj);
  return lshow;
}

