          break;
        }
      }
      if (matchedKey) break;
    }
    matchedKey = matchedKey ? Object.keys(iconMap).find(key => key.includes(matchedKey)) : null;
  }
  return matchedKey ? iconMap[matchedKey] : iconMap.default;
