    let baseStructure = url.split("?")[0].split('/').slice(3, -1).join('/');
    if (baseStructure.includes("-")) {
      let temp = baseStructure.split("-");
      baseStructure = temp[findIndicesOfElementsWithNumbers(temp)[0]];
      //log(baseStructure)
