function mergeStringsToJSON(str1, str2, type) {
  // 将 str1 按照 "&" 分割成数组
  let sstr2 = str2;
  let arr1 = str1.split("&");
  if (arr1[0] != "全部" && str2 == "*" && type != "by") {
    arr1.unshift("全部$");
  }
  if (str2 == "*") {
    str2 = undefined;
  }
  // 如果 str2 为空，初始化 arr2 为 arr1 的副本，每个元素的 "$" 后的部分为空字符串
  let arr2 = str2 ? str2.split("&") : arr1.map(item => item.includes("$") ? item.split("$")[1] : item);
  // 检查 arr1 和 arr2 的长度是否相等
  if (str2 && arr1.length !== arr2.length) {
    result;
  }
  if (arr1[0] != "全部" && arr2[0] != "" && sstr2 != "*" && type != "by") {
    arr1.unshift("全部");
    arr2.unshift("");
  }
  // 初始化结果数组
  let result = [];
  // 遍历 arr1 和 arr2
  for (let i = 0; i < arr1.length; i++) {
    let item1 = arr1[i].split("$")[0]; // 获取 "$" 前的值
    let item2 = arr2[i] || ""; // 获取 "$" 后的值或默认为空字符串
    // 将处理后的 arr1 和 arr2 的元素添加到结果数组
    result.push({ n: item1, v: item2 });
  }
  return result;
}

function getFilter(i, rule, name) {
  const aliasMap = new Map([
    ['电影', new Set(['电影', '電影'])],
    ['连续剧', new Set(['连续剧', '連續劇', /[剧劇]集/, /[电電][视視][剧劇]/])],
    ['综艺', new Set(['综艺', '綜藝', /综艺/])],
    ['动漫', new Set(['动漫', '動漫'])],
    ['纪录片', new Set(['纪录片'])]
  ]);
  function getOriginalByAlias(key) {
    for (let [k, v] of aliasMap) {
      for (let it of v) {
        if (it instanceof RegExp) {
          //正则表达;
          if (it.test(key)) {
            return k;
          }
        } else {
          //字符串;
          if (it == key) {
            return k;
          }
        }
      }
    }
    return key; // 原始值
  }
  name = getOriginalByAlias(name);
  //log(name)

  let items = new Map();
  let tempArray = [];
  let mapp = new Map([
    ["类型", "cateId"],
    ["剧情", "class"],
    ["地区", "area"],
    ["年份", "year"],
    ["语言", "lang"],
    ["字母", "letter"],
    ["排序", "by"]
  ])
  mapp.forEach((v, k, m) => {
    //log(k)
    let item = rule.getRuleValue([k]);
    let value = rule.getRuleValue([k + "值"]);
    //log(item)
    if (item && item != "*") {
      if (item.includes("||")) {
        item = item.split("||");
        //log(item)
        item.map((n, ni) => {
          if (n.includes("--")) {
            let parts = n.split("--");
            //parts[1] = "全部$&" + parts[1];
            items.set(parts[0], { v: parts[1], index: ni });
          }
        })
        if (items.size > 0) {
          item = items.get(name);
          if (typeof item == "object") {
            item = item.v;
          }
        } else {
          item = item[i];
        }
      }
      if (item == "空") {
        return;
      }
      if (item && item.includes("#")) {
        item = item.split("#").map(it => {
          if (it.includes('$')) {
            let parts = it.split('$');
            return {
              n: parts[0],
              v: parts[1]
            };
          } else {
            return {
              n: it,
              v: it
            };
          }
        });
        if (item[0].n != "全部" && v != "by") {
          item.unshift({
            n: "全部",
            v: ""
          })
        }
      }
      if (item && /^\d+-\d+$/.test(item)) {
        let [s, e] = item.split('-').map(Number);
        item = Array.from({ length: e - s + 1 }, (_, i) => e - i).join('&');
      }

      if (item && item.includes("&")) {
        item = mergeStringsToJSON(item, value, v);
      }
      if (!item) {
        return;
      }
      tempArray.push({
        key: v,
        name: k,
        value: item
      })
    }
  })
  return tempArray;
}


