            result = collapseSpacesAndTrim(result);
            //log(result)
            if (host) {
              if (result.startsWith("/")) {
                result = host + result;
              }
            }
            //log(content)
            break;
          }
        }
      }
    }
    if (type == "链接") {
      let es = /javascript:;/;
      if (Array.isArray(contents)) {
        for (let content of contents) {
          //log(content)
          result = XBPQParse.getHasRuleSplitStr(html, content);
          if (result != null && !es.test(result)) {
            result = collapseSpacesAndTrim(result);
