  category(tid, pg, filter, extend) {
    let host = getHome(this.getRuleValue(["分类url"]));
    if (!host.startsWith("http")) {
      host = getHome(this.getRuleValue(["主页url"]));
    }
    //log("host:" + host);

    let urlMap = new Map([
      ["{class}", {
        v: ["/class/{class}"]
      }],
      ["{area}", {
        v: ["/area/{area}"]
      }],
      ["{year}", {
        v: ["/year/{year}"]
      }],
      ["{area}", {
        v: ["/area/{area}"]
      }],
      ["{lang}", {
        v: ["/lang/{lang}"]
      }],
      ["{by}", {
        v: ["/by/{by}"]
      }],
    ]);
    //！！url含有[]会有问题，未解决
    let url = this.getRuleValue(["分类url"]);
    let firstPage = this.getRuleValue(["起始页", "分类起始页码", "qishiye", "firstpage"], "1");
    if (url.includes("[") && (url.includes("];") || /\]$/.test(url)) || url.includes("|")) {
      url = pg == firstPage ? url.replace(/.*[\[|\|].*(http[^\]]*)\]?.*/g, "$1").replace("firstPage=", "") : url.replace(/\|\|/g, "\\|").replace(/(.*)[\[|\|].*/g, "$1");
    }
    //log("f:"+url)
    let temp_url = this.splitTextWithSingleBracket(url);
    //log("f:"+temp_url)
    let sresult_url = host + url.replace(host, "");

    if (temp_url.filter(e => e).length > 1) {
      if (pg == 1) {
        url = host + temp_url[1].replace(host, "");
      } else {
        url = host + temp_url[0].replace(host, "");
      }
    } else {
      url = sresult_url;
    }

    for (let [key, value] of urlMap) {
      // 在这里进行判断
      if (url.includes(key)) {
        //console.log(`${key}`);
        let k = key.replace(/[{}]/g, "");
        if (extend.hasOwnProperty(k) && extend[k] != "") { } else {
          for (let vv of Array.from(value.v)) {
            if (url.includes(vv)) {
              url = url.replace(vv, "");
              break;
            }
          }
        }
      }
      // 可以根据需要添加更多的判断
    }
    //log("f:"+url)
    url = url.replace("{cateId}", extend.cateId || tid || "").replace("{class}", extend.class || "").replace("{area}", extend.area || "").replace("{year}", extend.year || "").replace("{by}", extend.by || "").replace("{lang}", extend.lang || "").replace("{catePg}", pg);

    if (url.includes(";;")) {
      let temp = url.split(";;");
      url = temp[0];
    }

    return this.一级(url, extend.cateId || tid);
  },
