/*
Ver 2023-5-25 1.3
*/

//url解析
function urlParse(u) {
    var dict = {}
    dict.Source = u;
    let params = (url) => {
        let queryParams = {};
        let reg = /([^?=&]+)=([^?=&]+)/g;
        url.replace(reg, function() {
            queryParams[arguments[1]] = arguments[2];
        })
        return queryParams
    }
    var javaUrl = JavaImporter(java.net.URL);
    with(javaUrl) {
        var url = new URL(u);
        dict.Protocol = url.getProtocol();
        dict.Host = url.getHost();
        dict.Port = url.getPort();
        dict.File = url.getFile();
        dict.Path = url.getPath();
        dict.Ref = url.getRef();
        dict.Hosts = `${dict.Protocol}://${url.getHost()}`
    }
    dict.Params = params(u);
    return dict;
}

//对象转查询字符串
Object.ToParams = function() {
    var json = arguments[0];
    return Object.keys(json).map(key => key + '=' + json[key]).join('&');
}

//判断两个对象是否相同
function isEqual(x, y) {
    const ok = Object.keys,
        tx = typeof x,
        ty = typeof y;
    return x && y && tx === 'object' && tx === ty ? (
        ok(x).length === ok(y).length &&
        ok(x).every(key => isEqual(x[key], y[key]))
    ) : (x === y);
}



//查询两个字符串不同处
function findDifferentIndex(str1, str2) {
    const diffChars = [];
    for (let i = 0; i < str2.length; i++) {
        if (str1[i] !== str2[i]) {
            diffChars.push({
                index: i,
                s1: str1[i],
                s2: str2[i]
            });
        }
    }
    return diffChars;
}

//替换字符串指定位置的字符串
function replaceCharAt(str, index, replacement) {
  if (index < 0 || index >= str.length) {
    return str;
  }
  //log(index)
  const arr = str.split("");
  arr[index] = replacement;
  return arr.join("");
}

//构建下一页
//参数1 源
//参数2 目标
//参数3 额外参数
function getNextUrl(source, target, params) {
    var u1 = urlParse(source);
    var u2 = urlParse(target);
    var pa=undefined;
    if(typeof(params)=='object'){
        pa = params.hasOwnProperty('特征')?params.特征:undefined;
    }
    var nexturl = source;
    var ca = Object.keys(u2.Params).length == 0 ? '' : '?';
    if (pa != undefined) {
        var rs = String(u2.Path).replace(new RegExp(pa), '$1fypage');
        nexturl = u2.Hosts + rs + ca + Object.ToParams(u2.Params);
    } else {
        if (isEqual(u1.Params, u2.Params)) {
            //log(u1.Path)
            //log(u2.Path)
            var df = findDifferentIndex(String(u1.Path), String(u2.Path));
            //log(df)
            if(df.length==1){
              var re=df[0].index
            }else{
               var f=df.filter(x=>/\d+/.test(x.s2));
               var re=f[0].index;
            }
            var eq = replaceCharAt(String(u2.Path), re, 'fypage');
            nexturl = u2.Hosts + eq + ca + Object.ToParams(u2.Params);
        } else {
            var eq = String(u1.Path).replace(/\d+/, 'fypage');
            nexturl = u1.Hosts + eq + ca+ Object.ToParams(u1.Params);
        }
    }
    //log('next:'+nexturl)
    return nexturl;
}

