function get_p_node() {
    // 引入加密解密库
    eval(getCryptoJS())

    // 获取p的加密节点数据
    var ciphertextBase64 = fetch('http://9966.fit/newline')
    ciphertextBase64 = ciphertextBase64.replace(/\s+/g, '')

    // 解密密钥（Base64编码）
    var keyBase64 = 'XKKhkRfwWO96rRQ3JSqNww=='
    var key = CryptoJS.enc.Base64.parse(keyBase64)

    // 解密IV（Base64编码）
    var ivBase64 = 'Ri09NSEyXS85Ryg8KD11WQ=='
    var iv = CryptoJS.enc.Base64.parse(ivBase64)

    // 解密
    var ciphertext = CryptoJS.enc.Base64.parse(ciphertextBase64)
    var decrypted = CryptoJS.AES.decrypt({ ciphertext: ciphertext }, key, {
        iv: iv,
        padding: CryptoJS.pad.Pkcs7,
        mode: CryptoJS.mode.CBC,
    })

    // 解密后的明文
    var plaintext = decrypted.toString(CryptoJS.enc.Utf8)

    var plaintext2 = CryptoJS.enc.Utf8.stringify(CryptoJS.enc.Base64.parse(plaintext))

    var res_arr = [plaintext, plaintext2]

    // 日志中打印p节点
    // console.log(res_arr[1])

    // 返回节点信息
    return res_arr
}

