// 首页缓存，便于快速生成分类、分类地址
function home_cache() {
    let home_res = getMyVar('home_cache', '')
    if (home_res == '') {
        home_res = fetch(config.home_url)
        putMyVar('home_cache', home_res)
    }
    return home_res
}

// 生成一、二级分类
function generate_category(level, res) {
    let html = ''
    let category_level_1_list = []
    let category_level_2_list = []
    switch (level) {
        case 1:
            html = home_cache()
            category_level_2_list = parseDomForArray(html, 'body&&.sub-menu&&ul&&li')

            for (let i = 0; i < category_level_2_list.length; i++) {
                let title = parseDomForHtml(category_level_2_list[i], 'a&&Text')
                res.push({
                    title: getVar('category_level_2_index', '0') == i ? style_color(title, '#EC6E61', 'strong') : title,
                    col_type: 'scroll_button',
                    url: $().lazyRule((index) => {
                        putVar('category_level_2_index', index)
                        refreshPage()
                        return 'hiker://empty'
                    }, i),
                })
            }
            break
        default:
            html = home_cache()
            category_level_1_list = parseDomForArray(html, 'body&&.header_menu&&ul&&li.menu-item-type-custom')
            category_level_1_list = category_level_1_list.concat(
                parseDomForArray(html, 'body&&.header_menu&&ul&&li.menu-item-object-category')
            )

            // 重新排了下第一分类的顺序
            let t_res = []
            let tt_res = []
            for (let i = 0; i < category_level_1_list.length; i++) {
                let title = parseDomForHtml(category_level_1_list[i], 'a&&Text')
                t_res.push({
                    title: getVar('category_level_1_index', '0') == i ? style_color(title, '#228B22', 'strong') : title,
                    col_type: 'flex_button',
                    url: $().lazyRule((index) => {
                        putVar('category_level_1_index', index)
                        putVar('category_level_2_index', '0')
                        refreshPage()
                        return 'hiker://empty'
                    }, i),
                })
                tt_res.push({
                    title: getVar('category_level_1_index', '0') == i ? style_color(title, '#228B22', 'strong') : title,
                    col_type: 'flex_button',
                    url: $().lazyRule((index) => {
                        putVar('category_level_1_index', index)
                        putVar('category_level_2_index', '0')
                        refreshPage()
                        return 'hiker://empty'
                    }, i),
                })
            }
            t_res[1] = tt_res[2]
            t_res[2] = tt_res[1]

            for (let i = 0; i < t_res.length; i++) {
                res.push(t_res[i])
            }
    }
}

// 获取分类地址
function generate_category_urls(mypage) {
    let category_urls = [
        [`https://meirentu.cc/index/${mypage}.html`],
        [],
        [`https://meirentu.cc/hots/${mypage}.html`],
        [`https://meirentu.cc/tags.html`],
    ]

    let html = home_cache()
    let urls = parseDomForArray(html, 'body&&.sub-menu&&li')

    for (let i = 0; i < urls.length; i++) {
        let actual_url = parseDomForHtml(urls[i], 'a&&href')
        if (actual_url.indexOf('https') == -1) {
            actual_url = 'https://meirentu.cc' + actual_url
        }
        category_urls[1].push(actual_url.replace(/\.html/, `-${mypage}.html`))
    }

    return category_urls
}

$.exports = {
    home_cache,
    generate_category,
    generate_category_urls,
}
